use rex::{Config, ChildFn, AttributeFn, ChildValue, AttributeValue}; use structx::*; pub type Props<T: Render> = Structx!{ products: Vec<Structx!{ name: T }> }; pub fn render<T: Render, C: Config>(props: &Props<T>) -> Vec<C::Node> {C::el("div", || HashMap::from([]), || vec![((props.products).iter().map(|product| {C::el("div", || HashMap::from([]), || vec![C::text(&(product).name)].flatten())}).collect::<Vec<_>>()).flatten()].flatten())}